        * 使用静态内部类完成单例设计模式!
        com.atguigu.principle.singleton.type07.Singleton@1540e19d
        356573597
        com.atguigu.principle.singleton.type07.Singleton@1540e19d
        356573597
        true
        com.atguigu.principle.singleton.type07.Singleton@1540e19d

        * */
    }
