}

package com.atguigu.bridge;


public abstract class Phone {
    // 组合品牌
    private Brand brand;

    public Phone(Brand brand) {
        super();
        this.brand = brand;
    }

    protected void open() {
        this.brand.open();
    }
    protected void close() {
        this.brand.close();
    }
    protected void call() {
        this.brand.call();
    }
}

package com.atguigu.bridge;

// 折叠是手机类,继承 抽象类Phone
public class FoldedPhone extends Phone{
    //  构造器
    public FoldedPhone(Brand brand) {
        // 初始化
        super(brand);
    }

    @Override
    protected void open() {
        super.open();
        System.out.println("打开折叠样式手机");
    }

    @Override
    protected void close() {
        super.close();
        System.out.println("关闭折叠样式手机");
    }

    @Override
    protected void call() {
        super.call();
        System.out.println("折叠样式手机打电话");
    }

}

package com.atguigu.bridge;


public class Client {
    public static void main(String[] args) {
        // 获取折叠式手机(样式 + 品牌
        Phone phone1 = new FoldedPhone(new XiaoMi());
        phone1.open();
        phone1.call();
        phone1.close();

        System.out.println("-----------");
        Phone phone2 = new FoldedPhone(new Vivo());
        phone2.open();
        phone2.call();
